ESX = nil TriggerEvent('esx:getSharedObject', function(obj) ESX = obj end) ESX.RegisterServerCallback('Snaily:DEVELOPMENT', function(source) local xPlayer = ESX.GetPlayerFromId(source) local coinsy = 250 if xPlayer.group == 'vip' then coinsy = coinsy * 2.0 end xPlayer.addInventoryItem("coins", coinsy) TriggerClientEvent('esx:showAdvancedNotification', xPlayer.source, '~o~SnailyDEV', "Powiadomienie", 'Otrzymałeś 250 [~y~Smoczych Monet~s~] Za 30 minut gry na Serwerze.') end)